import { useState, useEffect } from "react";

const DailyPrompt = () => {
  const today = new Date().toISOString().split("T")[0];
  const prompt = "‚ÄúWhat‚Äôs one thing that made you smile today?‚Äù";

  const saved = JSON.parse(localStorage.getItem("dailyPrompt")) || {};
  const [text, setText] = useState(saved[today] || "");
  const [submitted, setSubmitted] = useState(Boolean(saved[today]));

  // üî• Calculate streak
  const lastDates = Object.keys(saved).sort().reverse();
  let streak = 0;
  for (let i = 0; i < lastDates.length; i++) {
    const expected = new Date();
    expected.setDate(expected.getDate() - i);
    const formatted = expected.toISOString().split("T")[0];
    if (lastDates[i] === formatted) {
      streak++;
    } else {
      break;
    }
  }

  const handleSubmit = () => {
    const updated = { ...saved, [today]: text };
    localStorage.setItem("dailyPrompt", JSON.stringify(updated));
    setSubmitted(true);
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-lg font-semibold mb-2">üìù Daily Prompt</h2>
      <p className="mb-2">{prompt}</p>

      {!submitted ? (
        <>
          <textarea
            className="w-full p-2 border rounded"
            rows={4}
            placeholder="Write your reflection..."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <button
            onClick={handleSubmit}
            className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Submit
          </button>
        </>
      ) : (
        <p className="text-green-600 font-semibold">‚úÖ Submitted! Come back tomorrow.</p>
      )}

      {/* üî• Streak message */}
      {streak > 0 && (
        <p className="text-orange-600 font-semibold mt-4">
          üî• You're on a {streak}-day streak!<br />
          Keep showing up for yourself üí™
        </p>
      )}
    </div>
  );
};

export default DailyPrompt;
