import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";

const moods = ["üòÑ", "üôÇ", "üòê", "üòü", "üò≠"];

const Home = () => {
  const [selectedMood, setSelectedMood] = useState(
    localStorage.getItem("mood") || ""
  );

  const handleMoodClick = (mood) => {
    setSelectedMood(mood);
    localStorage.setItem("mood", mood);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />

      <main className="max-w-4xl mx-auto px-6 py-10 space-y-12">

        {/* Welcome Section */}
        <section>
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            Welcome to MindLoom üëã
          </h1>
          <p className="text-gray-600 text-lg">
            Build focus, clarity, and resilience‚Äîone mindful step at a time.
          </p>
        </section>

        {/* Mood Tracker */}
        <section className="bg-white rounded-xl shadow p-6">
          <h2 className="text-xl font-semibold mb-4">
            üß† How are you feeling today?
          </h2>
          <div className="flex gap-4 text-3xl">
            {moods.map((mood) => (
              <button
                key={mood}
                className={`transition hover:scale-110 ${
                  selectedMood === mood ? "ring-2 ring-blue-500" : ""
                }`}
                onClick={() => handleMoodClick(mood)}
              >
                {mood}
              </button>
            ))}
          </div>
          {selectedMood && (
            <p className="mt-4 text-sm text-gray-600">
              You selected: <span className="text-xl">{selectedMood}</span>
            </p>
          )}
        </section>

        {/* Daily Prompt */}
        <section className="bg-white rounded-xl shadow p-6">
          <h2 className="text-xl font-semibold mb-4">üìù Daily Prompt</h2>
          <p className="text-gray-700 mb-3">
            ‚ÄúWhat‚Äôs one thing that made you smile today?‚Äù
          </p>
          <textarea
            rows="4"
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Write your reflection..."
          ></textarea>
        </section>

        {/* Streak Counter */}
        <section className="bg-blue-100 border-l-4 border-blue-500 text-blue-800 p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold">üî• You're on a 3-day streak!</h3>
          <p className="text-sm">Keep showing up for yourself üí™</p>
        </section>

      </main>
    </div>
  );
};

export default Home;
