import { useState, useEffect } from "react";

const DailyPrompt = () => {
  const today = new Date().toISOString().split("T")[0]; // YYYY-MM-DD
  const [entry, setEntry] = useState("");
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("dailyPrompt")) || {};
    if (saved[today]) {
      setEntry(saved[today]);
      setSubmitted(true);
    }
  }, []);

  const handleSubmit = () => {
    const saved = JSON.parse(localStorage.getItem("dailyPrompt")) || {};
    saved[today] = entry;
    localStorage.setItem("dailyPrompt", JSON.stringify(saved));
    setSubmitted(true);
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-2">üìù Daily Reflection</h2>
      <textarea
        className="w-full border rounded-md p-2 mb-2"
        value={entry}
        onChange={(e) => setEntry(e.target.value)}
        disabled={submitted}
        rows={5}
        placeholder="What‚Äôs on your mind today?"
      />
      {!submitted && (
        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit
        </button>
      )}
      {submitted && (
        <p className="text-green-600">‚úÖ Submitted! Come back tomorrow.</p>
      )}
    </div>
  );
};

export default DailyPrompt;
